import React from 'react'
import {categories} from "../util.jsx/constants";
import { Stack } from '@mui/material';
const Sidebar = () => {
  return (
    <Stack direction="row" sx={{overflowY:"auto", height:{sx:"auto", md:"95%"}, flexDirection:{md:"column", sm:"row"}}}>
      {categories.map((category)=>(
        <button>
            <span>{category.icon}</span>
            <span>{category.name}</span>
        </button>
      ))}
    </Stack>
  )
}

export default Sidebar
