import React from 'react'
import {categories} from "../util.jsx/constants";
import { Stack } from '@mui/material';
const Sidebar = ({selectedCategory, setSelectedCategory}) => {
    // const selectedCategory = "New"
  return (
    <Stack direction="row" sx={{overflowY:"auto", height:{sx:"auto", md:"95%"}, flexDirection:{md:"column"}}}>
      {categories.map((category)=>(
        <button onClick={()=>setSelectedCategory(category.name)} className='category-btn' style={{
            background:category.name === selectedCategory && "#fc1503", color:"white"
        }} key={category.name}>
            <span style={{color:category.name === selectedCategory ? "white" :"red", marginRight:"15px"}}>{category.icon}</span>
            <span style={{opacity:category.name===selectedCategory?"1":"0.6"}}>{category.name}</span>
        </button>
      ))}
    </Stack>
  )
}

export default Sidebar
